import React, { useState, useEffect } from "react"; // useState, useEffect 임포트
import "./StockTable.css";
// import { FaRegHeart, FaHeart } from 'react-icons/fa'; // react-icons 사용 예시

export default function StockTable() {
  // --- 상태 관리 ---
  const [currentPage, setCurrentPage] = useState(1); // 현재 페이지 상태 (초기값 1)
  const [allChartData, setAllChartData] = useState([]); // 전체 차트 데이터 상태
  const [allChangeRankData, setAllChangeRankData] = useState([]); // 전체 변동순 데이터 상태 (필요하다면 이것도 관리)

  // --- 상수 ---
  const ITEMS_PER_PAGE = 10; // 페이지당 표시할 항목 수

  // --- 데이터 로딩 시뮬레이션 (실제로는 API 호출 등) ---
  useEffect(() => {
    // 예시: 전체 차트 데이터 (40개)
    const fetchedChartData = [
      { name: "HLB", price: "66,400원", change: -7.6, isFavorite: false },
      { name: "삼성전자", price: "60,200원", change: 2.9, isFavorite: true },
      { name: "SK하이닉스", price: "150,000원", change: 1.5, isFavorite: false },
      { name: "LG화학", price: "450,000원", change: -0.5, isFavorite: false },
      { name: "현대차", price: "200,000원", change: 0.8, isFavorite: false },
      { name: "기아", price: "85,000원", change: -1.1, isFavorite: false },
      { name: "POSCO홀딩스", price: "400,000원", change: 0.3, isFavorite: true },
      { name: "셀트리온", price: "180,000원", change: 2.1, isFavorite: false },
      { name: "KB금융", price: "60,000원", change: -0.2, isFavorite: false },
      { name: "신한지주", price: "40,000원", change: 0.1, isFavorite: false },
      // --- 페이지 2 데이터 ---
      { name: "카카오", price: "55,000원", change: -3.5, isFavorite: false },
      { name: "네이버", price: "210,000원", change: 4.2, isFavorite: false },
      { name: "LG에너지솔루션", price: "380,000원", change: -1.8, isFavorite: false },
      { name: "카카오페이", price: "48,000원", change: 5.5, isFavorite: false },
      { name: "두산에너빌리티", price: "17,000원", change: -2.0, isFavorite: false },
      { name: "하이브", price: "250,000원", change: 3.1, isFavorite: false },
      { name: "크래프톤", price: "230,000원", change: -1.5, isFavorite: false },
      { name: "SK이노베이션", price: "120,000원", change: 1.0, isFavorite: false },
      { name: "삼성SDI", price: "420,000원", change: -0.8, isFavorite: false },
      { name: "고려아연", price: "500,000원", change: 2.5, isFavorite: false },
      // --- 페이지 3 데이터 ---
       { name: "삼성물산", price: "140,000원", change: 0.7, isFavorite: false },
       { name: "LG전자", price: "100,000원", change: -0.4, isFavorite: false },
       { name: "KT&G", price: "90,000원", change: 1.2, isFavorite: false },
       { name: "SK텔레콤", price: "50,000원", change: -0.1, isFavorite: false },
       { name: "한국전력", price: "25,000원", change: 0.9, isFavorite: false },
       { name: "포스코퓨처엠", price: "300,000원", change: -2.2, isFavorite: false },
       { name: "HMM", price: "18,000원", change: 3.3, isFavorite: false },
       { name: "에코프로비엠", price: "260,000원", change: -1.3, isFavorite: false },
       { name: "엔씨소프트", price: "220,000원", change: 0.5, isFavorite: false },
       { name: "삼성생명", price: "75,000원", change: -0.7, isFavorite: false },
      // --- 페이지 4 데이터 ---
       { name: "아모레퍼시픽", price: "150,000원", change: 1.8, isFavorite: false },
       { name: "넷마블", price: "60,000원", change: -1.0, isFavorite: false },
       { name: "LG디스플레이", price: "15,000원", change: 2.0, isFavorite: false },
       { name: "SK바이오사이언스", price: "70,000원", change: -0.9, isFavorite: false },
       { name: "대한항공", price: "28,000원", change: 1.1, isFavorite: false },
       { name: "현대모비스", price: "230,000원", change: -0.3, isFavorite: false },
       { name: "삼성화재", price: "280,000원", change: 0.6, isFavorite: false },
       { name: "LG생활건강", price: "400,000원", change: -1.2, isFavorite: false },
       { name: "두산밥캣", price: "55,000원", change: 2.8, isFavorite: false },
       { name: "SK스퀘어", price: "45,000원", change: -0.6, isFavorite: false },
    ];
    setAllChartData(fetchedChartData); // 전체 데이터 상태 업데이트

    // 예시: 변동순 데이터 (이것도 필요하면 전체 데이터를 로드)
    const fetchedChangeRankData = [
       { name: "카카오페이", change: 5.5, isFavorite: false },
       { name: "네이버", change: 4.2, isFavorite: false },
       { name: "HMM", change: 3.3, isFavorite: false },
       { name: "하이브", change: 3.1, isFavorite: false },
       { name: "두산밥캣", change: 2.8, isFavorite: false },
       { name: "고려아연", change: 2.5, isFavorite: false },
       { name: "셀트리온", change: 2.1, isFavorite: false },
       { name: "LG디스플레이", change: 2.0, isFavorite: false },
       { name: "아모레퍼시픽", change: 1.8, isFavorite: false },
       { name: "KT&G", change: 1.2, isFavorite: false },
       { name: "대한항공", change: 1.1, isFavorite: false },
       { name: "SK이노베이션", change: 1.0, isFavorite: false },
       { name: "한국전력", change: 0.9, isFavorite: false },
       { name: "현대차", change: 0.8, isFavorite: false },
       { name: "삼성물산", change: 0.7, isFavorite: false },
       // ... (더 많은 변동순 데이터)
       // 변동순 데이터는 정렬 기준이 다르므로 별도 관리 필요
     ].sort((a, b) => b.change - a.change); // 예시: 변동률 내림차순 정렬
     setAllChangeRankData(fetchedChangeRankData);

  }, []); // 빈 배열을 넣어 컴포넌트 마운트 시 1회만 실행

  // --- 계산된 값 ---
  const totalPages = Math.ceil(allChartData.length / ITEMS_PER_PAGE); // 총 페이지 수 계산

  // 현재 페이지에 보여줄 데이터 계산
  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;
  const currentChartData = allChartData.slice(startIndex, endIndex); // 현재 페이지 데이터 슬라이스

  // --- 이벤트 핸들러 ---
  const handleFavoriteClick = (stockName) => {
    console.log(`${stockName} 찜 상태 변경됨`);
    // 실제 구현: allChartData 또는 allChangeRankData 상태 업데이트
    // 예시: setAllChartData(prevData => prevData.map(stock =>
    //   stock.name === stockName ? { ...stock, isFavorite: !stock.isFavorite } : stock
    // ));
    // 서버 API 호출 등...
  };

  // 페이지 변경 핸들러 (상태 업데이트 로직 추가)
  const handlePageChange = (page) => {
    if (page === 'prev') {
      setCurrentPage((prev) => Math.max(prev - 1, 1)); // 1 미만으로 내려가지 않도록
    } else if (page === 'next') {
      setCurrentPage((prev) => Math.min(prev + 1, totalPages)); // totalPages 초과하지 않도록
    } else {
      setCurrentPage(page); // 특정 페이지 번호로 이동
    }
    console.log(`페이지 ${page === 'prev' ? currentPage - 1 : page === 'next' ? currentPage + 1 : page}로 이동 요청됨 (실제 상태 업데이트는 다음 렌더링)`);
    // 데이터 다시 불러오기 로직은 페이지 변경 시 자동으로 currentChartData가 계산되므로 불필요 (단, 서버 페이징 시엔 필요)
  };

  // --- 페이지네이션 버튼 렌더링 로직 (기존 로직 활용) ---
  const renderPageNumbers = () => {
    const pageNumbers = [];
    const maxPagesToShow = 5; // 한 번에 보여줄 최대 페이지 버튼 수 (예: 1 2 3 4 5 ... 10)

    if (totalPages <= maxPagesToShow + 1) { // 페이지 수가 적을 때 (예: 6개 이하)
      for (let i = 1; i <= totalPages; i++) {
        pageNumbers.push(
          <button
            key={i}
            className={i === currentPage ? 'active' : ''}
            onClick={() => handlePageChange(i)}
          >
            {i}
          </button>
        );
      }
    } else { // 페이지 수가 많을 때
      let startPage = Math.max(1, currentPage - Math.floor(maxPagesToShow / 2));
      let endPage = Math.min(totalPages, startPage + maxPagesToShow - 1);

      // endPage 기준으로 startPage 다시 조정 (끝에 도달했을 때 버튼 개수 유지)
      if (endPage === totalPages) {
          startPage = Math.max(1, totalPages - maxPagesToShow + 1);
      }

      if (startPage > 1) {
        pageNumbers.push(
          <button key={1} onClick={() => handlePageChange(1)}>1</button>
        );
        if (startPage > 2) {
          pageNumbers.push(<span key="start-ellipsis">...</span>);
        }
      }

      for (let i = startPage; i <= endPage; i++) {
        pageNumbers.push(
          <button
            key={i}
            className={i === currentPage ? 'active' : ''}
            onClick={() => handlePageChange(i)}
          >
            {i}
          </button>
        );
      }

      if (endPage < totalPages) {
        if (endPage < totalPages - 1) {
          pageNumbers.push(<span key="end-ellipsis">...</span>);
        }
        pageNumbers.push(
          <button key={totalPages} onClick={() => handlePageChange(totalPages)}>{totalPages}</button>
        );
      }
    }

    return pageNumbers;
  };

  // --- 렌더링 ---
  return (
    <div className="stock-table">
      <div className="title">
        <h2 className="title-1">차트 (현재 {currentPage} / {totalPages} 페이지)</h2> {/* 페이지 정보 표시 */}
        <h2 className="title-2">변동 순</h2>
      </div>

      <div className="table-container">
        {/* 차트 테이블 */}
        <div className="chart-table">
          {/* 테이블 부분을 별도 div로 감싸서 스크롤 적용 (CSS 수정 필요 시 검토) */}
          <div style={{ flexGrow: 1, overflowY: 'auto', position: 'relative' }}>
             <table>
               <thead>
                 <tr>
                   <th className="rank-header">순위</th> {/* 헤더 텍스트 추가 */}
                   <th>종목</th>
                   <th>현재가</th>
                   <th>등락률</th>
                   <th className="favorite-header">찜</th> {/* 헤더 텍스트 추가 */}
                 </tr>
               </thead>
               <tbody>
                 {/* 현재 페이지 데이터(currentChartData)를 map으로 순회 */}
                 {currentChartData.map((stock, index) => (
                   // key는 고유한 값 사용 권장 (stock.name이 고유하다면 사용)
                   <tr key={stock.name} className="stock-row">
                     {/* 순위 계산: (현재 페이지 - 1) * 페이지당 항목 수 + 현재 인덱스 + 1 */}
                     <td className="rank-cell">{(currentPage - 1) * ITEMS_PER_PAGE + index + 1}</td>
                     <td>{stock.name}</td>
                     <td>{stock.price}</td>
                     <td className={stock.change >= 0 ? "positive" : "negative"}>
                       {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                     </td>
                     <td className="favorite-cell">
                       <span
                         // 찜 상태에 따라 아이콘/스타일 변경 (예시: isFavorite 사용)
                         className={`favorite-icon ${stock.isFavorite ? 'active' : ''}`}
                         onClick={() => handleFavoriteClick(stock.name)}
                         title={`${stock.name} 찜하기/해제`}
                       >
                         {/* react-icons 사용 예시: {stock.isFavorite ? <FaHeart /> : <FaRegHeart />} */}
                         ♥
                       </span>
                     </td>
                   </tr>
                 ))}
               </tbody>
             </table>
          </div>
          {/* 페이지네이션 컨트롤 */}
          {totalPages > 1 && ( // 페이지가 1개 초과일 때만 페이지네이션 표시
            <div className="pagination-controls">
              <button onClick={() => handlePageChange('prev')} disabled={currentPage === 1}>&lt;</button>
              {renderPageNumbers()} {/* 동적으로 생성된 페이지 번호 버튼들 */}
              <button onClick={() => handlePageChange('next')} disabled={currentPage === totalPages}>&gt;</button>
            </div>
          )}
        </div>

        {/* 세로선 */}
        <div className="vertical-line"></div>

        {/* 변동순 테이블 */}
        <div className="change-rank">
          <table>
            <thead>
              <tr>
                <th>종목</th>
                <th>등락률</th>
                <th className="favorite-header">찜</th>
              </tr>
            </thead>
            <tbody>
              {/* 변동순 데이터는 상위 N개만 표시 (예: 11개) */}
              {allChangeRankData.slice(0, 11).map((stock, index) => (
                <tr key={stock.name} className="stock-row"> {/* 고유 key 사용 */}
                  <td>{stock.name}</td>
                  <td className={stock.change >= 0 ? "positive" : "negative"}>
                    {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                  </td>
                  <td className="favorite-cell">
                    <span
                      className={`favorite-icon ${stock.isFavorite ? 'active' : ''}`} // 찜 상태 반영
                      onClick={() => handleFavoriteClick(stock.name)}
                      title={`${stock.name} 찜하기/해제`}
                    >
                      ♥
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          {/* 변동순 테이블에는 페이지네이션 없음 */}
        </div>
      </div>
    </div>
  );
}
