// StockTable.jsx
import React, { useState, useEffect } from "react";
import "./StockTable.css";
// import { FaRegHeart, FaHeart } from 'react-icons/fa'; // react-icons 사용 예시

// onStockSelect prop을 받도록 수정
export default function StockTable({ onStockSelect }) {
  // --- 상태 관리 ---
  const [currentPage, setCurrentPage] = useState(1);
  const [allChartData, setAllChartData] = useState([]);
  const [allChangeRankData, setAllChangeRankData] = useState([]);
  const [loading, setLoading] = useState(true); // 로딩 상태 추가
  const [error, setError] = useState(null); // 에러 상태 추가

  // --- 상수 ---
  const ITEMS_PER_PAGE = 10;
  const IP = "172.17.153.62:8080"; // API 및 로고 서버 IP

  // --- 데이터 로딩 ---
  useEffect(() => {
    // 1. 차트 데이터 로딩 (초기 목록 + 개별 상세 정보)
    const fetchChartData = async () => {
      try {
        // 1-1. 종목 목록 (이름, 티커) 가져오기
        const listResponse = await fetch(`http://${IP}/recommend/kr`);
        if (!listResponse.ok) {
          throw new Error(`종목 목록 로딩 실패: ${listResponse.status}`);
        }
        const initialStockList = await listResponse.json();

        if (!Array.isArray(initialStockList) || initialStockList.length === 0) {
          console.log("추천 종목 목록이 비어있습니다.");
          setAllChartData([]);
          return;
        }

        // 1-2. 각 티커에 대한 상세 정보 (전일종가, 등락률) 병렬 요청
        const detailPromises = initialStockList.map(async (stock) => {
          if (!stock.ticker || !stock.stock_name) {
            console.warn("종목 목록 데이터에 ticker 또는 stock_name이 없습니다:", stock);
            return { name: stock.stock_name || "이름 없음", ticker: stock.ticker || "티커 없음", price: "N/A", change: 0.0 };
          }

          try {
            const detailResponse = await fetch(`http://${IP}/changerate/kr/${stock.ticker}`);
            if (!detailResponse.ok) {
              console.warn(`티커 ${stock.ticker} 상세 정보 로딩 실패: ${detailResponse.status}`);
              return { name: stock.stock_name, ticker: stock.ticker, price: "N/A", change: 0.0 };
            }
            const details = await detailResponse.json();
            const yesterdayClose = details.yesterday_close;
            const changeRate = details.change_rate;

            return {
              name: stock.stock_name,
              ticker: stock.ticker,
              price: typeof yesterdayClose === 'number' ? yesterdayClose : "N/A",
              change: typeof changeRate === 'number' ? changeRate : 0.0,
            };
          } catch (e) {
            console.error(`티커 ${stock.ticker} 상세 정보 fetch 또는 처리 중 오류:`, e);
            return { name: stock.stock_name, ticker: stock.ticker, price: "N/A", change: 0.0 };
          }
        });

        const combinedDataResults = await Promise.allSettled(detailPromises);

        const finalChartData = combinedDataResults
          .filter(result => result.status === 'fulfilled' && result.value) // 성공하고 값이 있는 경우만
          .map(result => result.value);

        setAllChartData(finalChartData);

      } catch (e) {
        console.error("차트 데이터 로딩 중 전체 오류 발생:", e);
        setError(`데이터 로딩 실패: ${e.message}`);
        setAllChartData([]);
      }
    };

    // 2. 변동순 데이터 로딩
    const setupChangeRankData = async () => {
      // !!! 중요: 실제로는 이 데이터도 서버에서 ticker를 포함하여 가져와야 합니다. !!!
      // 현재는 예시 데이터를 사용하지만, 서버에서 API를 통해 가져오는 로직으로 대체해야 합니다.
      // 예를 들어, `http://${IP}/changerank/kr` 와 같은 엔드포인트가 있다고 가정합니다.
      // const response = await fetch(`http://${IP}/changerank/kr`);
      // if (!response.ok) throw new Error('변동순 데이터 로딩 실패');
      // const fetchedChangeRankData = await response.json();
      // 아래는 현재 하드코딩된 데이터
      const fetchedChangeRankData = [
        { name: "카카오페이", ticker: "377300", change: 5.5 },
        { name: "네이버", ticker: "035420", change: 4.2 },
        { name: "HMM", ticker: "011200", change: 3.3 },
        { name: "하이브", ticker: "352820", change: 3.1 },
        { name: "두산밥캣", ticker: "241560", change: 2.8 },
        { name: "고려아연", ticker: "010130", change: 2.5 },
        { name: "셀트리온", ticker: "068270", change: 2.1 },
        { name: "LG디스플레이", ticker: "034220", change: 2.0 },
        { name: "아모레퍼시픽", ticker: "090430", change: 1.8 },
        { name: "KT&G", ticker: "033780", change: 1.2 },
        { name: "대한항공", ticker: "003490", change: 1.1 },
        { name: "SK이노베이션", ticker: "096770", change: 1.0 },
      ].sort((a, b) => b.change - a.change);
      setAllChangeRankData(fetchedChangeRankData);
    };

    // 데이터 로딩 실행
    const loadAllData = async () => {
        setLoading(true);
        setError(null);
        try {
            await fetchChartData();
            await setupChangeRankData(); // 변동순 데이터도 비동기일 수 있으므로 await
        } catch (e) {
            console.error("데이터 로딩 중 오류:", e);
            // setError를 fetchChartData 내부에서 이미 처리하고 있으므로,
            // 여기서는 추가적인 전역 에러 처리가 필요하면 추가합니다.
            // 만약 setupChangeRankData도 실패할 수 있다면 여기서 setError를 설정할 수 있습니다.
        } finally {
            setLoading(false); // 모든 데이터 로딩 시도 후 로딩 상태 종료
        }
    };

    loadAllData();

  }, []); // 컴포넌트 마운트 시 1회 실행

  const totalPages = Math.ceil(allChartData.length / ITEMS_PER_PAGE);
  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;
  const currentChartData = allChartData.slice(startIndex, endIndex);

  const handleFavoriteClick = (stockTicker, stockName) => { // 티커도 받을 수 있도록 수정
    console.log(`종목: ${stockName} (티커: ${stockTicker}) 찜 상태 변경됨`);
    // 여기에 실제 찜 로직 추가 (예: API 호출 또는 로컬 상태 변경)
  };

  const handlePageChange = (page) => {
    if (page === 'prev') {
      setCurrentPage((prev) => Math.max(prev - 1, 1));
    } else if (page === 'next') {
      setCurrentPage((prev) => Math.min(prev + 1, totalPages));
    } else {
      setCurrentPage(page);
    }
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];
    const maxPagesToShow = 5; // 페이지네이션에 한 번에 표시할 최대 페이지 수

    if (totalPages <= maxPagesToShow + 1) { // 전체 페이지 수가 적을 때 (생략 부호 필요 없음)
        for (let i = 1; i <= totalPages; i++) {
            pageNumbers.push(
                <button
                    key={i}
                    className={i === currentPage ? 'active' : ''}
                    onClick={() => handlePageChange(i)}
                >
                    {i}
                </button>
            );
        }
    } else { // 전체 페이지 수가 많아 생략 부호가 필요할 수 있을 때
        let startPage = Math.max(1, currentPage - Math.floor(maxPagesToShow / 2));
        let endPage = Math.min(totalPages, startPage + maxPagesToShow - 1);

        // endPage가 totalPages에 도달했지만, startPage 때문에 maxPagesToShow만큼 표시되지 않는 경우 조정
        if (endPage === totalPages) {
            startPage = Math.max(1, totalPages - maxPagesToShow + 1);
        }
        // startPage가 1이지만, endPage 때문에 maxPagesToShow만큼 표시되지 않는 경우 조정
         if (startPage === 1 && (endPage - startPage + 1) < maxPagesToShow) {
            endPage = Math.min(totalPages, startPage + maxPagesToShow - 1);
        }


        if (startPage > 1) {
            pageNumbers.push(
                <button key={1} onClick={() => handlePageChange(1)}>1</button>
            );
            if (startPage > 2) {
                pageNumbers.push(<span key="start-ellipsis">...</span>);
            }
        }

        for (let i = startPage; i <= endPage; i++) {
            pageNumbers.push(
                <button
                    key={i}
                    className={i === currentPage ? 'active' : ''}
                    onClick={() => handlePageChange(i)}
                >
                    {i}
                </button>
            );
        }

        if (endPage < totalPages) {
            if (endPage < totalPages - 1) {
                pageNumbers.push(<span key="end-ellipsis">...</span>);
            }
            pageNumbers.push(
                <button key={totalPages} onClick={() => handlePageChange(totalPages)}>{totalPages}</button>
            );
        }
    }
    return pageNumbers;
  };


  if (loading) {
    return <div className="stock-table loading">데이터를 불러오는 중입니다...</div>;
  }

  if (error) {
    return <div className="stock-table error">오류: {error}</div>;
  }

  return (
    <div className="stock-table">
      <div className="title">
        <h2 className="title-1">추천 종목</h2> {/* 제목 변경 */}
        <h2 className="title-2">주요 변동순</h2> {/* 제목 변경 */}
      </div>

      <div className="table-container">
        <div className="chart-table"> {/* 이 div가 차트 테이블과 페이지네이션을 감쌈 */}
            <div style={{ flexGrow: 1, overflowY: 'auto', position: 'relative' }}> {/* 이 div가 테이블 스크롤을 담당 */}
                <table>
                    <thead>
                    <tr>
                        <th className="rank-header"></th>
                        <th>종목</th>
                        <th>현재가</th>
                        <th>등락률</th>
                        <th className="favorite-header"></th>
                    </tr>
                    </thead>
                    <tbody>
                    {/* currentChartData가 비어있을 경우 메시지 표시 */}
                    {currentChartData.length === 0 && !loading ? (
                        <tr>
                            <td colSpan="5" style={{ textAlign: 'center', padding: '20px' }}>표시할 추천 종목이 없습니다.</td>
                        </tr>
                    ) : (
                        currentChartData.map((stock, index) => (
                        // stock.ticker가 고유하다고 가정하고 key로 사용
                        // onStockSelect를 호출하여 선택된 stock 객체 전달
                        <tr key={stock.ticker || index} className="stock-row" onClick={() => onStockSelect && onStockSelect(stock)}>
                            <td className="rank-cell">{(currentPage - 1) * ITEMS_PER_PAGE + index + 1}</td>
                            <td className="stock-name-cell">
                            {stock.ticker && ( // 티커가 있을 때만 로고 표시
                                <img
                                src={`http://${IP}/logo/kr/${stock.ticker}`}
                                alt={`${stock.name} 로고`}
                                className="stock-logo"
                                onError={(e) => { e.target.style.display = 'none'; }}
                                />
                            )}
                            <span>{stock.name || "이름 없음"}</span>
                            </td>
                            <td>
                            {typeof stock.price === 'number'
                                ? `${stock.price.toLocaleString('ko-KR')}원`
                                : stock.price === "N/A" ? "N/A" : "가격 정보 없음"}
                            </td>
                            <td className={stock.change >= 0 ? "positive" : "negative"}>
                            {typeof stock.change === 'number' ?
                                `${stock.change >= 0 ? '+' : ''}${stock.change.toFixed(1)}%` : "N/A"}
                            </td>
                            <td className="favorite-cell">
                            <span
                                className="favorite-icon" // 'active' 클래스 추가/제거 로직 필요
                                onClick={(e) => {
                                    e.stopPropagation(); // 행 클릭 이벤트 전파 방지
                                    handleFavoriteClick(stock.ticker, stock.name);
                                }}
                                title={`${stock.name} 찜하기`}
                            >
                                ♥
                            </span>
                            </td>
                        </tr>
                        ))
                    )}
                    </tbody>
                </table>
            </div>
            {totalPages > 1 && (
            <div className="pagination-controls">
                <button onClick={() => handlePageChange('prev')} disabled={currentPage === 1}>&lt;</button>
                {renderPageNumbers()}
                <button onClick={() => handlePageChange('next')} disabled={currentPage === totalPages}>&gt;</button>
            </div>
            )}
        </div>

        <div className="vertical-line"></div>

        <div className="change-rank">
            <table>
            <thead>
                <tr>
                <th>종목</th>
                <th>등락률</th>
                <th className="favorite-header"></th>
                </tr>
            </thead>
            <tbody>
                {allChangeRankData.slice(0, 10).map((stock) => (
                // 변동순 목록의 항목도 클릭 가능하게 하려면 onClick 핸들러 추가
                <tr key={stock.ticker || stock.name} className="stock-row" onClick={() => onStockSelect && onStockSelect(stock)}>
                    <td className="stock-name-cell">
                    {stock.ticker && (
                        <img
                        // 변동순 테이블의 로고 IP도 동일한 IP를 사용한다고 가정
                        src={`http://${IP}/logo/kr/${stock.ticker}`}
                        alt={`${stock.name} 로고`}
                        className="stock-logo"
                        onError={(e) => { e.target.style.display = 'none'; }}
                        />
                    )}
                    <span>{stock.name}</span>
                    </td>
                    <td className={stock.change >= 0 ? "positive" : "negative"}>
                    {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                    </td>
                    <td className="favorite-cell">
                    <span
                        className="favorite-icon"
                        onClick={(e) => {
                            e.stopPropagation();
                            handleFavoriteClick(stock.ticker, stock.name);
                        }}
                        title={`${stock.name} 찜하기`}
                    >
                        ♥
                    </span>
                    </td>
                </tr>
                ))}
            </tbody>
            </table>
        </div>
      </div>
    </div>
  );
}
