import React from 'react';
// import { useState } from 'react'; // useState는 이제 필요 없습니다.
import { HomeIcon, HeartIcon, ClockIcon } from '@heroicons/react/24/solid';
import logo from './img/logo.png';
import './Sidebar.css';

// 부모로부터 onFavoriteClick 함수를 props로 받습니다.
export default function Sidebar({ onFavoriteClick }) {
  // const [isFavoriteExpanded, setIsFavoriteExpanded] = useState(false); // 상태 제거
  // const handleFavoriteClick = () => { ... }; // 핸들러 제거

  return (
    <aside className="sidebar">
      <div className="logo">
        <img src={logo} alt="로고" className="logo-img" />
        <span className="web-name"></span>
      </div>
      {/*
      <div className="icon-text">
        <HomeIcon className="icon" />
        <span className="icon-label">홈</span>
      </div>
      */}

      {/* '관심' 영역 */}
      <div
        // className={`icon-text ${isFavoriteExpanded ? 'expanded' : ''}`} // expanded 클래스 제거
        className="icon-text" // 기본 클래스만 사용
        onClick={onFavoriteClick} // 부모로부터 받은 함수를 클릭 시 호출
      >
        <HeartIcon className="icon" />
        <span className="icon-label">관심</span>
      </div>

      {/* '최근 본' 영역 */}
      <div className="icon-text">
        <ClockIcon className="icon" />
        <span className="icon-label">최근 본</span>
      </div>
    </aside>
  );
}
