import React, { useState } from "react";
import "./StockTable.css";

export default function StockTable() {
  // 상태 추가 (페이지 관리용)
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10; // 한 페이지에 보여줄 항목 수

  // 기존 데이터 (40개로 확장)
  const allChartData = [
    { name: "HLB", price: "66,400원", change: -7.6 },
    { name: "삼성전자", price: "60,200원", change: 2.9 },
    { name: "SK하이닉스", price: "150,000원", change: 1.5 },
    { name: "LG화학", price: "450,000원", change: -0.5 },
    { name: "현대차", price: "200,000원", change: 0.8 },
    { name: "기아", price: "85,000원", change: -1.1 },
    { name: "POSCO홀딩스", price: "400,000원", change: 0.3 },
    { name: "셀트리온", price: "180,000원", change: 2.1 },
    { name: "KB금융", price: "60,000원", change: -0.2 },
    { name: "신한지주", price: "40,000원", change: 0.1 },
    // 추가 데이터 (총 40개)
    { name: "카카오", price: "50,000원", change: -3.5 },
    { name: "네이버", price: "220,000원", change: 4.2 },
    { name: "LG에너지솔루션", price: "450,000원", change: -1.8 },
    { name: "카카오페이", price: "60,000원", change: 5.5 },
    { name: "두산에너빌리티", price: "22,000원", change: -2.0 },
    { name: "하이브", price: "180,000원", change: 3.1 },
    { name: "크래프톤", price: "250,000원", change: -1.5 },
    { name: "SK이노베이션", price: "150,000원", change: 1.0 },
    { name: "삼성SDI", price: "550,000원", change: -0.8 },
    { name: "고려아연", price: "480,000원", change: 2.5 },
    { name: "LG전자", price: "110,000원", change: 1.2 },
    { name: "삼성생명", price: "70,000원", change: -0.5 },
    { name: "한국전력", price: "20,000원", change: 0.3 },
    { name: "KT&G", price: "90,000원", change: 1.1 },
    { name: "삼성물산", price: "120,000원", change: -2.3 },
    { name: "현대모비스", price: "220,000원", change: 0.7 },
    { name: "NAVER", price: "210,000원", change: 3.2 },
    { name: "삼성전기", price: "140,000원", change: -1.2 },
    { name: "LG", price: "80,000원", change: 0.5 },
    { name: "삼성화재", price: "75,000원", change: -0.3 },
    { name: "아모레퍼시픽", price: "130,000원", change: 2.1 },
    { name: "한미약품", price: "320,000원", change: -3.1 },
    { name: "셀트리온헬스케어", price: "90,000원", change: 1.8 },
    { name: "에코프로", price: "280,000원", change: 4.2 },
    { name: "삼성바이오로직스", price: "820,000원", change: -2.5 },
    { name: "현대글로비스", price: "180,000원", change: 0.9 },
    { name: "SK텔레콤", price: "50,000원", change: -0.7 },
    { name: "한국조선해양", price: "110,000원", change: 3.5 }
  ];

  // 현재 페이지에 보여줄 데이터 계산
  const chartTableData = allChartData.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // 전체 페이지 수 계산
  const totalPages = Math.ceil(allChartData.length / itemsPerPage);

  // 기존 함수 유지
  const handleFavoriteClick = (stockName) => {
    console.log(`${stockName} 찜 상태 변경됨`);
  };

  // 페이지 변경 함수 수정
  const handlePageChange = (pageNumber) => {
    if (pageNumber === 'prev' && currentPage > 1) {
      setCurrentPage(currentPage - 1);
    } else if (pageNumber === 'next' && currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    } else if (typeof pageNumber === 'number') {
      setCurrentPage(pageNumber);
    }
    console.log(`페이지 ${currentPage}로 이동`);
  };

  // 기존 변동순 데이터 유지
  const changeRankData = [
    { name: "카카오", change: -3.5 },
    { name: "네이버", change: 4.2 },
    { name: "LG에너지솔루션", change: -1.8 },
    { name: "카카오페이", change: 5.5 },
    { name: "두산에너빌리티", change: -2.0 },
    { name: "하이브", change: 3.1 },
    { name: "크래프톤", change: -1.5 },
    { name: "SK이노베이션", change: 1.0 },
    { name: "삼성SDI", change: -0.8 },
    { name: "고려아연", change: 2.5 },
  ];

  // 기존 렌더링 로직 유지 (단, 페이지네이션 부분만 수정)
  return (
    <div className="stock-table">
      {/* ... 기존 title, table-container 등 모든 구조 유지 ... */}
      
      {/* 차트 테이블 부분 - 기존 구조 유지하되 데이터만 변경 */}
      <div className="chart-table">
        <table>
          {/* ... thead 유지 ... */}
          <tbody>
            {chartTableData.map((stock, index) => (
              <tr key={(currentPage - 1) * itemsPerPage + index} className="stock-row">
                <td className="rank-cell">{(currentPage - 1) * itemsPerPage + index + 1}</td>
                <td>{stock.name}</td>
                <td>{stock.price}</td>
                <td className={stock.change >= 0 ? "positive" : "negative"}>
                  {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                </td>
                <td className="favorite-cell">
                  <span
                    className="favorite-icon"
                    onClick={() => handleFavoriteClick(stock.name)}
                    title={`${stock.name} 찜하기`}
                  >
                    ♥
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        
        {/* 페이지네이션 컨트롤 - 기존 구조 유지하되 로직만 변경 */}
        <div className="pagination-controls">
          <button onClick={() => handlePageChange('prev')} disabled={currentPage === 1}>&lt;</button>
          
          {/* 첫 페이지 표시 */}
          <button
            className={1 === currentPage ? 'active' : ''}
            onClick={() => handlePageChange(1)}
          >
            1
          </button>
          
          {/* 중간 생략 표시 (조건부 렌더링) */}
          {currentPage > 3 && <span>...</span>}
          
          {/* 현재 페이지 주변 페이지 표시 */}
          {Array.from({ length: totalPages }, (_, i) => i + 1)
            .filter(page => page > 1 && page < totalPages && Math.abs(page - currentPage) <= 1)
            .map(page => (
              <button
                key={page}
                className={page === currentPage ? 'active' : ''}
                onClick={() => handlePageChange(page)}
              >
                {page}
              </button>
            ))}
          
          {/* 끝 생략 표시 (조건부 렌더링) */}
          {currentPage < totalPages - 2 && <span>...</span>}
          
          {/* 마지막 페이지 표시 (totalPages > 1 일 때만) */}
          {totalPages > 1 && (
            <button
              className={totalPages === currentPage ? 'active' : ''}
              onClick={() => handlePageChange(totalPages)}
            >
              {totalPages}
            </button>
          )}
          
          <button onClick={() => handlePageChange('next')} disabled={currentPage === totalPages}>&gt;</button>
        </div>
      </div>
      
      {/* ... 기존 세로선, 변동순 테이블 등 모든 구조 유지 ... */}
    </div>
  );
}
