// Sidebar.js
import React from 'react';
import { HeartIcon, ClockIcon } from '@heroicons/react/24/solid';
import logo from './img/logo.png';
import './Sidebar.css';

export default function Sidebar({ onFavoriteClick, onRecentClick }) {
  const handleFavoriteClick = () => {
    console.log('관심 아이콘 클릭됨!'); // <-- 콘솔 로그 추가
    if (onFavoriteClick) { // props로 함수가 잘 전달되었는지 확인
      onFavoriteClick();
    } else {
      console.error('onFavoriteClick prop이 전달되지 않았습니다.');
    }
  };

  const handleRecentClick = () => {
    console.log('최근 본 아이콘 클릭됨!'); // <-- 콘솔 로그 추가 (최근 본 기능 구현 시)
    if (onRecentClick) {
      onRecentClick();
    } else {
      console.error('onRecentClick prop이 전달되지 않았습니다.');
    }
  };

  return (
    <aside className="sidebar">
      {/* ... 로고 ... */}
      <div
        className="icon-text"
        onClick={handleFavoriteClick} // 내부 핸들러 사용
        style={{ cursor: 'pointer' }}
      >
        <HeartIcon className="icon" />
        <span className="icon-label">관심</span>
      </div>
      <div
        className="icon-text"
        onClick={handleRecentClick} // 내부 핸들러 사용
        style={{ cursor: 'pointer' }}
      >
        <ClockIcon className="icon" />
        <span className="icon-label">최근 본</span>
      </div>
    </aside>
  );
}
