// ChartSection.jsx
import React from 'react';
import './ChartSection.css'; // ChartSection.css에 positive/negative 스타일이 정의되어 있다고 가정

const formatDate = (date) => {
  const year = date.getFullYear();
  const month = (date.getMonth() + 1).toString().padStart(2, '0');
  const day = date.getDate().toString().padStart(2, '0');
  return `${year}${month}${day}`;
};

export default function ChartSection({ ticker, stockName, stockPrice, stockChange, logoUrl, chartServerIp }) {

  const handleChartAnalysis = () => {
    console.log(`차트 분석 버튼 클릭됨: ${ticker}`);
  };

  const handleOtherAction = () => {
    console.log(`다른 기능 버튼 클릭됨: ${ticker}`);
  };

  const today = new Date();
  //const fiveYearsAgo = new Date(new Date().setFullYear(today.getFullYear() - 5));
  // 사용자가 제공한 코드에서는 시작날짜가 20000101, 종료날짜가 20250423으로 되어있어 이를 우선 반영합니다.
  // 필요시 동적 날짜로 변경 가능합니다.
  const startDate = "20000101"; // 사용자의 원본 iframe src 참고
  const endDate = "20250423";   // 사용자의 원본 iframe src 참고
  // const startDate = formatDate(fiveYearsAgo);
  // const endDate = formatDate(today);


  const chartIframeSrc = `http://${chartServerIp}/chart/kr/${ticker}/${startDate}/${endDate}`;

  if (!ticker) {
    return (
      <div className="chart-section">
        <p>종목 정보를 불러올 수 없습니다. 목록에서 종목을 선택해주세요.</p>
      </div>
    );
  }

  // 등락률 표시를 위한 클래스와 텍스트 포맷팅
  const changeClassName = stockChange >= 0 ? "positive" : "negative";
  const changeText = `${stockChange >= 0 ? '+' : ''}${typeof stockChange === 'number' ? stockChange.toFixed(1) : 'N/A'}%`;


  return (
    <div className="chart-section">
      <div className="chart-header-info">
        {logoUrl ? (
          <img
            src={logoUrl}
            alt={`${stockName || '종목'} 로고`}
            className="image-placeholder"
            onError={(e) => {
              e.target.style.display = 'none';
              const parent = e.target.parentNode;
              if (parent && parent.classList.contains('image-placeholder')) {
                parent.textContent = '로고';
              }
            }}
          />
        ) : (
          <div className="image-placeholder">로고</div>
        )}
        <div className="stock-info-text">
          <span className="stock-name">{stockName || ticker}</span>
          {/* 가격과 등락률을 함께 표시 */}
          <div className="stock-price-details">
            <span className="stock-price">{stockPrice}</span>
            {/* 등락률 표시. StockTable.css의 positive/negative 스타일 재사용 또는 ChartSection.css에 정의 필요 */}
            {typeof stockChange === 'number' && (
                <span className={`stock-change ${changeClassName}`} style={{ marginLeft: '8px' }}>
                    {changeText}
                </span>
            )}
          </div>
        </div>
      </div>

      <div className="chart-layout-container">
        <div className="chart-iframe-container">
          <iframe
            key={ticker}
            className="chart-iframe"
            src={chartIframeSrc}
            title={`${stockName || ticker} Stock Chart`}
          ></iframe>
        </div>
        <div className="chart-controls">
          <h3>AI 분석 ({stockName || ticker})</h3>
          <button onClick={handleChartAnalysis}>차트 분석</button>
          <button onClick={handleOtherAction}>다른 기능 1</button>
          <button onClick={handleOtherAction}>다른 기능 2</button>
        </div>
      </div>
    </div>
  );
}
