import React from 'react';
import { HeartIcon, ClockIcon } from '@heroicons/react/24/solid'; // HomeIcon 제거됨 (주석처리됨)
import logo from './img/logo.png';
import './Sidebar.css';

// 부모로부터 onFavoriteClick 함수를 props로 받습니다.
export default function Sidebar({ onFavoriteClick, onRecentClick }) {
  // ... (컴포넌트 상단 또는 return 전)
  // console.log('Sidebar 렌더링'); // 필요시 추가
  return (
    <aside className="sidebar">
       {/* ... */}
       <div className="icon-text" onClick={onFavoriteClick} style={{ cursor: 'pointer' }}> {/* 바로 prop 사용 */}
         <HeartIcon className="icon" />
         <span className="icon-label">관심</span>
       </div>
       <div className="icon-text" onClick={onRecentClick} style={{ cursor: 'pointer' }}> {/* 바로 prop 사용 */}
         <ClockIcon className="icon" />
         <span className="icon-label">최근 본</span>
       </div>
    </aside>
  );
}
