import React from "react";
import "./StockTable.css";
// 아이콘 라이브러리(예: react-icons) 사용을 고려해볼 수 있습니다.
// import { FaRegHeart, FaHeart } from 'react-icons/fa'; // react-icons 사용 예시

export default function StockTable() {

  // 찜 클릭 처리를 위한 임시 함수
  // 실제 앱에서는 상태를 업데이트하거나 API를 호출할 것입니다.
  const handleFavoriteClick = (stockName) => {
    console.log(`${stockName} 찜 상태 변경됨`);
    // 여기에 찜 상태 관리 로직 추가 (예: 상태 업데이트, API 호출)
  };

  // 페이지네이션 클릭 처리를 위한 임시 함수
  const handlePageChange = (pageNumber) => {
    console.log(`페이지 ${pageNumber}로 이동`);
    // 실제 페이지 변경 로직 (데이터 다시 불러오기 등) 추가 필요
  };

  // 예시 데이터 (실제 데이터 소스로 교체해야 합니다)
  // 페이지네이션을 고려하면 이 데이터는 현재 페이지에 해당하는 부분만 있어야 합니다.
  const chartTableData = [
    { name: "HLB", price: "66,400원", change: -7.6 },
    { name: "삼성전자", price: "60,200원", change: 2.9 },
    { name: "SK하이닉스", price: "150,000원", change: 1.5 },
    { name: "LG화학", price: "450,000원", change: -0.5 },
    { name: "현대차", price: "200,000원", change: 0.8 },
    { name: "기아", price: "85,000원", change: -1.1 },
    { name: "POSCO홀딩스", price: "400,000원", change: 0.3 },
    { name: "셀트리온", price: "180,000원", change: 2.1 },
    { name: "KB금융", price: "60,000원", change: -0.2 },
    { name: "신한지주", price: "40,000원", change: 0.1 },
    // 실제로는 한 페이지에 보여줄 만큼의 데이터만 있어야 함 (예: 10개)
  ];

  // 현재 페이지 (예시, 실제로는 상태로 관리)
  const currentPage = 1;
  // 전체 페이지 수 (예시, 실제로는 데이터 총 개수와 페이지당 항목 수로 계산)
  const totalPages = 10;

  const changeRankData = [
    { name: "카카오", change: -3.5 },
    { name: "네이버", change: 4.2 },
    { name: "LG에너지솔루션", change: -1.8 },
    { name: "카카오페이", change: 5.5 },
    { name: "두산에너빌리티", change: -2.0 },
    { name: "하이브", change: 3.1 },
    { name: "크래프톤", change: -1.5 },
    { name: "SK이노베이션", change: 1.0 },
    { name: "삼성SDI", change: -0.8 },
    { name: "고려아연", change: 2.5 },
    // ... 더 많은 데이터
  ];


  return (
    <div className="stock-table">
      <div className="title">
        <h2 className="title-1">차트</h2>
        <h2 className="title-2">변동 순</h2>
      </div>

      <div className="table-container">
        {/* 차트 테이블 */}
        <div className="chart-table">
          <table>
            <thead>
              <tr>
                <th className="rank-header">#</th> {/* 순위 헤더 추가 */}
                <th>종목</th>
                <th>현재가</th>
                <th>등락률</th>
                <th className="favorite-header">찜</th> {/* 찜 헤더 */}
              </tr>
            </thead>
            <tbody>
              {/* 데이터를 map으로 순회하며 행 생성 */}
              {chartTableData.map((stock, index) => (
                // 실제 페이지네이션 구현 시 index 대신 고유 ID와 페이지 번호를 조합하여 key 사용 권장
                <tr key={index} className="stock-row"> {/* 호버 효과를 위한 클래스 */}
                  <td className="rank-cell">{index + 1}</td> {/* 순위 표시 셀 추가 */}
                  <td>{stock.name}</td>
                  <td>{stock.price}</td>
                  <td className={stock.change >= 0 ? "positive" : "negative"}>
                    {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                  </td>
                  <td className="favorite-cell"> {/* 찜 아이콘 셀 */}
                    <span
                      className="favorite-icon"
                      onClick={() => handleFavoriteClick(stock.name)}
                      title={`${stock.name} 찜하기`}
                    >
                      ♥
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          {/* 페이지네이션 컨트롤 */}
          <div className="pagination-controls">
            <button onClick={() => handlePageChange('prev')} disabled={currentPage === 1}>&lt;</button> {/* 이전 버튼 */}
            {/* 페이지 번호 (실제 구현 시 동적으로 생성) */}
            {[...Array(totalPages)].map((_, i) => i + 1).slice(0, 5).map(page => ( // 예시로 5개만 표시
              <button
                key={page}
                className={page === currentPage ? 'active' : ''} // 현재 페이지 활성화 스타일
                onClick={() => handlePageChange(page)}
              >
                {page}
              </button>
            ))}
            {totalPages > 5 && <span>...</span>} {/* 페이지 많을 경우 생략 표시 */}
            {totalPages > 5 && ( // 마지막 페이지 번호 표시 (totalPages > 5 인 경우)
                 <button
                     key={totalPages}
                     className={totalPages === currentPage ? 'active' : ''}
                     onClick={() => handlePageChange(totalPages)}
                 >
                     {totalPages}
                 </button>
             )}
            <button onClick={() => handlePageChange('next')} disabled={currentPage === totalPages}>&gt;</button> {/* 다음 버튼 */}
          </div>
        </div>

        {/* 세로선 */}
        <div className="vertical-line"></div>

        {/* 변동순 테이블 */}
        <div className="change-rank">
          <table>
            <thead>
              <tr>
                <th>종목</th>
                <th>등락률</th>
                <th className="favorite-header"></th> {/* 찜 헤더 */}
              </tr>
            </thead>
            <tbody>
               {/* 데이터를 map으로 순회하며 행 생성 */}
              {changeRankData.slice(0, 11).map((stock, index) => ( // 예시로 11개만 표시
                 <tr key={index} className="stock-row"> {/* 호버 효과 클래스 */}
                  <td>{stock.name}</td>
                  <td className={stock.change >= 0 ? "positive" : "negative"}>
                     {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                  </td>
                   <td className="favorite-cell"> {/* 찜 아이콘 셀 */}
                     <span
                       className="favorite-icon"
                       onClick={() => handleFavoriteClick(stock.name)}
                       title={`${stock.name} 찜하기`}
                     >
                       ♥
                     </span>
                   </td>
                 </tr>
               ))}
            </tbody>
          </table>
          {/* 변동순 테이블에는 페이지네이션 없음 */}
        </div>
      </div>
    </div>
  );
}
