import React, { useState } from 'react';
import './ChatBox.css'; // CSS 연결

function ChatBox() {
  const [input, setInput] = useState('');
  const [reply, setReply] = useState('');

  const handleSubmit = async () => {
    const API_KEY = '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'; // 테스트용으로만!
    const response = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${API_KEY}`,
      },
      body: JSON.stringify({
        model: 'gpt-3.5-turbo',
        messages: [{ role: 'user', content: input }],
      }),
    });

    const data = await response.json();
    const content = data.choices[0].message.content;
    setReply(content);
  };

  return (
    <div className="chat-container">
      <textarea
        className="chat-input"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="질문을 입력하세요..."
      />
      <button className="chat-button" onClick={handleSubmit}>
        GPT에게 물어보기
      </button>
      <div className="chat-response">
        <strong>GPT:</strong>
        <p>{reply}</p>
      </div>
    </div>
  );
}

export default ChatBox;
