import React, { useState, useEffect, useRef } from 'react';
import './Header.css';

export default function Header() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [message, setMessage] = useState('');
  const [showResults, setShowResults] = useState(false); // Í≤∞Í≥º Ï∞Ω ÌëúÏãú Ïó¨Î∂Ä
  const searchContainerRef = useRef(null); // Í≤ÄÏÉâ ÏòÅÏó≠ Ï∞∏Ï°∞

  // --- Í≤ÄÏÉâÏ∞Ω Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄ Hook ---
  useEffect(() => {
    function handleClickOutside(event) {
      // searchContainerRef.currentÍ∞Ä Ï°¥Ïû¨ÌïòÍ≥†, ÌÅ¥Î¶≠Îêú ÏöîÏÜåÍ∞Ä searchContainerRef.current ÎÇ¥Î∂ÄÏóê ÏóÜÏúºÎ©¥
      if (searchContainerRef.current && !searchContainerRef.current.contains(event.target)) {
        setShowResults(false); // Í≤∞Í≥º Ï∞Ω Ïà®Í∏∞Í∏∞
      }
    }
    // Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù
    document.addEventListener("mousedown", handleClickOutside);
    // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Ï†úÍ±∞
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [searchContainerRef]);

  // --- Í≤ÄÏÉâÏñ¥ Î≥ÄÍ≤Ω Í∞êÏßÄ Î∞è API Ìò∏Ï∂ú Hook ---
  useEffect(() => {
    // Í≤ÄÏÉâÏñ¥Í∞Ä ÎπÑÏñ¥ÏûàÏúºÎ©¥ Í≤∞Í≥ºÏôÄ Î©îÏãúÏßÄÎ•º Ï¥àÍ∏∞ÌôîÌïòÍ≥† Ïà®ÍπÄ
    if (!query.trim()) {
      setResults([]);
      setMessage('');
      setShowResults(false);
      return;
    }

    setShowResults(true); // ÏûÖÎ†• Ïãú Í≤∞Í≥º Ï∞Ω ÌëúÏãú

    // ÎîîÎ∞îÏö¥Ïã± ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï
    const timeoutId = setTimeout(async () => {
      try {
        // !!! Ï§ëÏöî: '/search?q=' Î∂ÄÎ∂ÑÏùÑ Ïã§Ï†ú Ïö¥ÏòÅÌïòÏãúÎäî Î∞±ÏóîÎìú API ÏóîÎìúÌè¨Ïù∏Ìä∏Î°ú ÍµêÏ≤¥Ìï¥Ïïº Ìï©ÎãàÎã§. !!!
        // Î∞±ÏóîÎìúÏóêÏÑúÎäî ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞(q)Î•º Î∞õÏïÑ Ï£ºÏãù Ï†ïÎ≥¥Î•º Í≤ÄÏÉâÌïòÍ≥† JSON ÌòïÌÉúÎ°ú Î∞òÌôòÌï¥Ïïº Ìï©ÎãàÎã§.
        // Ïòà: const res = await fetch(`https://your-api.com/stocks?query=${encodeURIComponent(query)}`);

        // --- Ïó¨Í∏∞ÏÑúÎäî ÏòàÏãúÎ•º ÏúÑÌï¥ Mock APIÎ•º ÏÇ¨Ïö©Ìï©ÎãàÎã§ ---
        const mockFetch = (q) => {
          return new Promise(resolve => {
            setTimeout(() => {
              const mockData = [
                { ticker: "AAPL", name: "Apple Inc.", source: "NASDAQ" },
                { ticker: "GOOGL", name: "Alphabet Inc.", source: "NASDAQ" },
                { ticker: "MSFT", name: "Microsoft Corp.", source: "NASDAQ" },
                { ticker: "AMZN", name: "Amazon.com, Inc.", source: "NASDAQ" },
                { ticker: "TSLA", name: "Tesla, Inc.", source: "NASDAQ" },
                { ticker: "005930", name: "ÏÇºÏÑ±Ï†ÑÏûê", source: "KOSPI" },
                { ticker: "035420", name: "NAVER", source: "KOSPI" },
              ].filter(stock =>
                stock.name.toLowerCase().includes(q.toLowerCase()) ||
                stock.ticker.toLowerCase().includes(q.toLowerCase())
              );
              resolve({ ok: true, json: () => Promise.resolve(mockData) });
            }, 150); // ÎÑ§Ìä∏ÏõåÌÅ¨ ÏßÄÏó∞ ÏãúÎÆ¨Î†àÏù¥ÏÖò
          });
        };
        const res = await mockFetch(query); // Ïã§Ï†úÎ°úÎäî: await fetch(...)
        // --- Mock API ÎÅù ---

        if (!res.ok) {
          throw new Error('ÎÑ§Ìä∏ÏõåÌÅ¨ ÏùëÎãµÏù¥ Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.');
        }

        const data = await res.json();

        if (data.length === 0) {
          setResults([]);
          setMessage('Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.');
        } else {
          setResults(data);
          setMessage('');
        }
      } catch (error) {
        console.error('Í≤ÄÏÉâ Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
        setResults([]);
        setMessage('Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    }, 300); // 300ms ÎîîÎ∞îÏö¥Ïä§

    // cleanup Ìï®Ïàò: Ïù¥Ï†Ñ ÌÉÄÏù¥Î®∏Î•º Ï∑®ÏÜåÌïòÏó¨ ÎßàÏßÄÎßâ ÏûÖÎ†• ÌõÑ 300msÍ∞Ä ÏßÄÎÇòÎ©¥ API Ìò∏Ï∂ú
    return () => clearTimeout(timeoutId);
  }, [query]); // 'query' ÏÉÅÌÉúÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Ïù¥ HookÏùÑ Ïã§Ìñâ

  // ÏûÖÎ†•Ï∞Ω Ìï∏Îì§Îü¨
  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  return (
    <header className="header">
      {/* Í≤ÄÏÉâÏ∞ΩÍ≥º Í≤∞Í≥ºÎ•º Í∞êÏã∏Îäî Ïª®ÌÖåÏù¥ÎÑà */}
      <div className="search-container" ref={searchContainerRef}>
        <input
          className="search-input"
          placeholder="üîç Ï¢ÖÎ™©Î™Ö ÎòêÎäî Ìã∞Ïª§ Í≤ÄÏÉâ"
          value={query}
          onChange={handleInputChange}
          onFocus={() => query && setShowResults(true)} // Ìè¨Ïª§Ïä§ Ïãú Í≤∞Í≥ºÍ∞Ä ÏûàÏúºÎ©¥ Î≥¥Ïó¨Ï§å
          autoComplete="off" // ÏûêÎèôÏôÑÏÑ± ÎÅÑÍ∏∞
        />
        {/* showResultsÍ∞Ä trueÏù¥Í≥†, Í≤ÄÏÉâÏñ¥Í∞Ä ÏûàÏùÑ ÎïåÎßå Í≤∞Í≥º Ï∞Ω ÌëúÏãú */}
        {showResults && query.length > 0 && (
          <div className="search-results">
            {results.length > 0 && (
              <ul>
                {results.map(({ ticker, name, source }) => (
                  <li key={ticker}>
                    {`[${ticker}] ${name} (${source})`}
                    {/* onClick Ìï∏Îì§Îü¨Î•º Ï∂îÍ∞ÄÌïòÏó¨ Ï¢ÖÎ™© ÏÑ†ÌÉù Ïãú ÎèôÏûë Íµ¨ÌòÑ Í∞ÄÎä• */}
                  </li>
                ))}
              </ul>
            )}
            {message && <div className="search-message">{message}</div>}
          </div>
        )}
      </div>
      <button className="login-btn">Î°úÍ∑∏Ïù∏</button>
    </header>
  );
}
