import React from 'react';
import { HeartIcon, ClockIcon } from '@heroicons/react/24/solid'; // HomeIcon 제거됨 (주석처리됨)
import logo from './img/logo.png';
import './Sidebar.css';

// 부모로부터 onFavoriteClick 함수를 props로 받습니다.
export default function Sidebar({ onFavoriteClick, onRecentClick }) { // onRecentClick 추가 (필요하다면)
  return (
    <aside className="sidebar">
      <div className="logo">
        <img src={logo} alt="로고" className="logo-img" />
        <span className="web-name"></span>
      </div>
      {/*
      <div className="icon-text">
        <HomeIcon className="icon" />
        <span className="icon-label">홈</span>
      </div>
      */}

      {/* '관심' 영역 */}
      <div
        className="icon-text"
        onClick={onFavoriteClick} // 부모로부터 받은 함수를 클릭 시 호출
        style={{ cursor: 'pointer' }} // 클릭 가능함을 시각적으로 표시
      >
        <HeartIcon className="icon" />
        <span className="icon-label">관심</span>
      </div>

      {/* '최근 본' 영역 */}
      <div
        className="icon-text"
        // onClick={onRecentClick} // 필요하다면 최근 본 패널용 핸들러 연결
        style={{ cursor: 'pointer' }} // 클릭 가능함을 시각적으로 표시
       >
        <ClockIcon className="icon" />
        <span className="icon-label">최근 본</span>
      </div>
    </aside>
  );
}
