import React, { useState, useEffect, useRef } from 'react';
import './Header.css';

export default function Header() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [message, setMessage] = useState('');
  const [showResults, setShowResults] = useState(false);
  const searchContainerRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (searchContainerRef.current && !searchContainerRef.current.contains(event.target)) {
        setShowResults(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [searchContainerRef]);

  useEffect(() => {
    if (!query.trim()) {
      setResults([]);
      setMessage('');
      setShowResults(false);
      return;
    }

    setShowResults(true);

    const timeoutId = setTimeout(async () => {
      try {
        // --- ⬇️ 여기를 수정했습니다: 실제 서버 API 호출 ---
        const apiUrl = `http://172.17.154.182:5000/search?q=${encodeURIComponent(query)}`;
        console.log(`Workspaceing from: ${apiUrl}`); // 디버깅을 위해 콘솔에 URL 출력

        const res = await fetch(apiUrl);
        // --- ⬆️ 여기까지 수정했습니다 ---

        if (!res.ok) {
          // 서버에서 오류 응답 시 더 자세한 정보 확인
          const errorText = await res.text();
          throw new Error(`네트워크 응답 오류: ${res.status} - ${errorText}`);
        }

        const data = await res.json(); // 응답을 JSON으로 파싱

        if (data.length === 0) {
          setResults([]);
          setMessage('검색 결과가 없습니다.');
        } else {
          setResults(data);
          setMessage('');
        }
      } catch (error) {
        console.error('검색 중 오류 발생:', error);
        setResults([]);
        // CORS 오류 또는 네트워크 연결 실패 시 메시지 표시
        if (error.message.includes('Failed to fetch')) {
             setMessage('서버 연결에 실패했습니다. CORS 또는 네트워크 설정을 확인하세요.');
        } else {
             setMessage('검색 중 오류가 발생했습니다.');
        }
      }
    }, 300); // 300ms 디바운스

    return () => clearTimeout(timeoutId);
  }, [query]);

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  return (
    <header className="header">
      <div className="search-container" ref={searchContainerRef}>
        <input
          className="search-input"
          placeholder="🔍 종목명 또는 티커 검색"
          value={query}
          onChange={handleInputChange}
          onFocus={() => query && setShowResults(true)}
          autoComplete="off"
        />
        {showResults && query.length > 0 && (
          <div className="search-results">
            {results.length > 0 && (
              <ul>
                {/* 서버에서 받은 데이터 구조에 맞게 key와 내용을 조정하세요 */}
                {results.map((item, index) => (
                  <li key={item.ticker || index}> {/* 고유한 key가 필요합니다. 'ticker'가 있다면 사용하고, 없다면 index를 사용합니다. */}
                    {`[${item.ticker}] ${item.name} (${item.source})`}
                  </li>
                ))}
              </ul>
            )}
            {message && <div className="search-message">{message}</div>}
          </div>
        )}
      </div>
      <button className="login-btn">로그인</button>
    </header>
  );
}
