import React, { useState, useEffect, useRef } from 'react';
import './Header.css';

export default function Header() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [message, setMessage] = useState('');
  const [showResults, setShowResults] = useState(false);
  const searchContainerRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (searchContainerRef.current && !searchContainerRef.current.contains(event.target)) {
        setShowResults(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [searchContainerRef]);

  useEffect(() => {
    if (!query.trim()) {
      setResults([]);
      setMessage('');
      setShowResults(false);
      return;
    }

    setShowResults(true);

    const timeoutId = setTimeout(async () => {
      try {
        // --- ‚¨áÔ∏è Ïó¨Í∏∞Î•º ÏàòÏ†ïÌñàÏäµÎãàÎã§: Ïã§Ï†ú ÏÑúÎ≤Ñ API Ìò∏Ï∂ú ---
        const apiUrl = `http://172.17.154.182:5000/search?q=${encodeURIComponent(query)}`;
        console.log(`Workspaceing from: ${apiUrl}`); // ÎîîÎ≤ÑÍπÖÏùÑ ÏúÑÌï¥ ÏΩòÏÜîÏóê URL Ï∂úÎ†•

        const res = await fetch(apiUrl);
        // --- ‚¨ÜÔ∏è Ïó¨Í∏∞ÍπåÏßÄ ÏàòÏ†ïÌñàÏäµÎãàÎã§ ---

        if (!res.ok) {
          // ÏÑúÎ≤ÑÏóêÏÑú Ïò§Î•ò ÏùëÎãµ Ïãú Îçî ÏûêÏÑ∏Ìïú Ï†ïÎ≥¥ ÌôïÏù∏
          const errorText = await res.text();
          throw new Error(`ÎÑ§Ìä∏ÏõåÌÅ¨ ÏùëÎãµ Ïò§Î•ò: ${res.status} - ${errorText}`);
        }

        const data = await res.json(); // ÏùëÎãµÏùÑ JSONÏúºÎ°ú ÌååÏã±

        if (data.length === 0) {
          setResults([]);
          setMessage('Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.');
        } else {
          setResults(data);
          setMessage('');
        }
      } catch (error) {
        console.error('Í≤ÄÏÉâ Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
        setResults([]);
        // CORS Ïò§Î•ò ÎòêÎäî ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞ Ïã§Ìå® Ïãú Î©îÏãúÏßÄ ÌëúÏãú
        if (error.message.includes('Failed to fetch')) {
             setMessage('ÏÑúÎ≤Ñ Ïó∞Í≤∞Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. CORS ÎòêÎäî ÎÑ§Ìä∏ÏõåÌÅ¨ ÏÑ§Ï†ïÏùÑ ÌôïÏù∏ÌïòÏÑ∏Ïöî.');
        } else {
             setMessage('Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
        }
      }
    }, 300); // 300ms ÎîîÎ∞îÏö¥Ïä§

    return () => clearTimeout(timeoutId);
  }, [query]);

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  return (
    <header className="header">
      <div className="search-container" ref={searchContainerRef}>
        <input
          className="search-input"
          placeholder="üîç Ï¢ÖÎ™©Î™Ö ÎòêÎäî Ìã∞Ïª§ Í≤ÄÏÉâ"
          value={query}
          onChange={handleInputChange}
          onFocus={() => query && setShowResults(true)}
          autoComplete="off"
        />
        {showResults && query.length > 0 && (
          <div className="search-results">
            {results.length > 0 && (
              <ul>
                {/* ÏÑúÎ≤ÑÏóêÏÑú Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞ Íµ¨Ï°∞Ïóê ÎßûÍ≤å keyÏôÄ ÎÇ¥Ïö©ÏùÑ Ï°∞Ï†ïÌïòÏÑ∏Ïöî */}
                {results.map((item, index) => (
                  <li key={item.ticker || index}> {/* Í≥†Ïú†Ìïú keyÍ∞Ä ÌïÑÏöîÌï©ÎãàÎã§. 'ticker'Í∞Ä ÏûàÎã§Î©¥ ÏÇ¨Ïö©ÌïòÍ≥†, ÏóÜÎã§Î©¥ indexÎ•º ÏÇ¨Ïö©Ìï©ÎãàÎã§. */}
                    {`[${item.ticker}] ${item.name} (${item.source})`}
                  </li>
                ))}
              </ul>
            )}
            {message && <div className="search-message">{message}</div>}
          </div>
        )}
      </div>
      <button className="login-btn">Î°úÍ∑∏Ïù∏</button>
    </header>
  );
}
