import React, { useState } from "react";
import "./StockTable.css";

export default function StockTable() {
  // 상태 관리
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10; // 한 페이지에 보여줄 항목 수

  // 찜 상태 관리를 위한 상태 (예시)
  const [favorites, setFavorites] = useState({});

  // 찜 클릭 처리
  const handleFavoriteClick = (stockName) => {
    setFavorites(prev => ({
      ...prev,
      [stockName]: !prev[stockName]
    }));
    console.log(`${stockName} 찜 상태: ${!favorites[stockName]}`);
  };

  // 페이지 변경 처리
  const handlePageChange = (page) => {
    if (page === 'prev' && currentPage > 1) {
      setCurrentPage(currentPage - 1);
    } else if (page === 'next' && currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    } else if (typeof page === 'number') {
      setCurrentPage(page);
    }
  };

  // 예시 데이터 (40개)
  const allChartData = Array.from({ length: 40 }, (_, i) => ({
    name: `종목 ${i + 1}`,
    price: `${Math.floor(Math.random() * 50000) + 10000}원`,
    change: (Math.random() * 10 - 5).toFixed(1)
  }));

  // 변동순 데이터 (고정 10개)
  const changeRankData = [
    { name: "카카오", change: -3.5 },
    { name: "네이버", change: 4.2 },
    { name: "LG에너지솔루션", change: -1.8 },
    { name: "카카오페이", change: 5.5 },
    { name: "두산에너빌리티", change: -2.0 },
    { name: "하이브", change: 3.1 },
    { name: "크래프톤", change: -1.5 },
    { name: "SK이노베이션", change: 1.0 },
    { name: "삼성SDI", change: -0.8 },
    { name: "고려아연", change: 2.5 }
  ];

  // 현재 페이지 데이터 계산
  const chartTableData = allChartData.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // 전체 페이지 수 계산
  const totalPages = Math.ceil(allChartData.length / itemsPerPage);

  // 페이지네이션 번호 생성 (최대 5개까지 표시)
  const getPageNumbers = () => {
    const pages = [];
    const maxVisiblePages = 5;
    
    // 항상 첫 페이지 포함
    pages.push(1);
    
    // 현재 페이지 주변 페이지 계산
    let startPage = Math.max(2, currentPage - 1);
    let endPage = Math.min(totalPages - 1, currentPage + 1);
    
    // 시작 페이지와 끝 페이지 조정
    if (currentPage <= 3) {
      endPage = Math.min(4, totalPages - 1);
    } else if (currentPage >= totalPages - 2) {
      startPage = Math.max(totalPages - 3, 2);
    }
    
    // 중간 페이지 추가
    for (let i = startPage; i <= endPage; i++) {
      if (!pages.includes(i)) pages.push(i);
    }
    
    // 마지막 페이지 추가
    if (totalPages > 1 && !pages.includes(totalPages)) {
      pages.push(totalPages);
    }
    
    return pages;
  };

  return (
    <div className="stock-table">
      <div className="title">
        <h2 className="title-1">차트</h2>
        <h2 className="title-2">변동 순</h2>
      </div>

      <div className="table-container">
        {/* 차트 테이블 */}
        <div className="chart-table">
          <table>
            <thead>
              <tr>
                <th className="rank-header"></th>
                <th>종목</th>
                <th>현재가</th>
                <th>등락률</th>
                <th className="favorite-header"></th>
              </tr>
            </thead>
            <tbody>
              {chartTableData.map((stock, index) => (
                <tr key={(currentPage - 1) * itemsPerPage + index} className="stock-row">
                  <td className="rank-cell">{(currentPage - 1) * itemsPerPage + index + 1}</td>
                  <td>{stock.name}</td>
                  <td>{stock.price}</td>
                  <td className={stock.change >= 0 ? "positive" : "negative"}>
                    {stock.change >= 0 ? '+' : ''}{stock.change}%
                  </td>
                  <td className="favorite-cell">
                    <span
                      className={`favorite-icon ${favorites[stock.name] ? 'active' : ''}`}
                      onClick={() => handleFavoriteClick(stock.name)}
                      title={`${stock.name} 찜하기`}
                    >
                      {favorites[stock.name] ? '♥' : '♡'}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          
          {/* 페이지네이션 컨트롤 */}
          <div className="pagination-controls">
            <button 
              onClick={() => handlePageChange('prev')} 
              disabled={currentPage === 1}
            >
              &lt;
            </button>
            
            {getPageNumbers().map((page, idx, array) => (
              <React.Fragment key={page}>
                {idx > 0 && page - array[idx - 1] > 1 && (
                  <span>...</span>
                )}
                <button
                  className={page === currentPage ? 'active' : ''}
                  onClick={() => handlePageChange(page)}
                >
                  {page}
                </button>
              </React.Fragment>
            ))}
            
            <button 
              onClick={() => handlePageChange('next')} 
              disabled={currentPage === totalPages}
            >
              &gt;
            </button>
          </div>
        </div>

        {/* 세로선 */}
        <div className="vertical-line"></div>

        {/* 변동순 테이블 */}
        <div className="change-rank">
          <table>
            <thead>
              <tr>
                <th>종목</th>
                <th>등락률</th>
                <th className="favorite-header"></th>
              </tr>
            </thead>
            <tbody>
              {changeRankData.map((stock, index) => (
                <tr key={index} className="stock-row">
                  <td>{stock.name}</td>
                  <td className={stock.change >= 0 ? "positive" : "negative"}>
                    {stock.change >= 0 ? '+' : ''}{stock.change}%
                  </td>
                  <td className="favorite-cell">
                    <span
                      className={`favorite-icon ${favorites[stock.name] ? 'active' : ''}`}
                      onClick={() => handleFavoriteClick(stock.name)}
                      title={`${stock.name} 찜하기`}
                    >
                      {favorites[stock.name] ? '♥' : '♡'}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
