  import "./StockTable.css";
  // import { FaRegHeart, FaHeart } from 'react-icons/fa'; // react-icons 사용 예시

  export default function StockTable() {
    // --- 상태 관리 ---
    const [currentPage, setCurrentPage] = useState(1);
    const [allChartData, setAllChartData] = useState([]);
    const [allChangeRankData, setAllChangeRankData] = useState([]);
    const [loading, setLoading] = useState(true); // 로딩 상태 추가
    const [error, setError] = useState(null); // 에러 상태 추가

    // --- 상수 ---
    const ITEMS_PER_PAGE = 10;
    const IP = "172.17.153.62:8080";

    // --- 데이터 로딩 ---
    useEffect(() => {
      // 1. 차트 데이터 로딩 (초기 목록 + 개별 상세 정보)
      const fetchChartData = async () => {
          try {
              // 1-1. 종목 목록 (이름, 티커) 가져오기
              const listResponse = await fetch(`http://${IP}/recommend/kr`);
              if (!listResponse.ok) {
                  throw new Error(`종목 목록 로딩 실패: ${listResponse.status}`);
              }
              const initialStockList = await listResponse.json();

              if (!Array.isArray(initialStockList) || initialStockList.length === 0) {
                  console.log("추천 종목 목록이 비어있습니다.");
                  setAllChartData([]);
                  return;
              }

              // 1-2. 각 티커에 대한 상세 정보 (전일종가, 등락률) 병렬 요청
              const detailPromises = initialStockList.map(async (stock) => {
                  if (!stock.ticker || !stock.stock_name) {
                      console.warn("종목 목록 데이터에 ticker 또는 stock_name이 없습니다:", stock);
                      return { name: stock.stock_name || "이름 없음", ticker: stock.ticker || "티커 없음", price: "N/A", change: 0.0 };
                  }

                  try {
                      const detailResponse = await fetch(`http://172.17.153.62:8080/changerate/kr/${stock.ticker}`);
                      if (!detailResponse.ok) {
                          console.warn(`티커 ${stock.ticker} 상세 정보 로딩 실패: ${detailResponse.status}`);
                          return { name: stock.stock_name, ticker: stock.ticker, price: "N/A", change: 0.0 };
                      }
                      const details = await detailResponse.json();

                      // details 객체에서 필요한 값 추출
                      const yesterdayClose = details.yesterday_close;
                      const changeRate = details.change_rate;

                      // *** 현재가 계산 로직 제거 ***
                      // price 상태에 yesterday_close 값을 직접 할당
                      // change 상태에 change_rate 값을 직접 할당

                      return {
                          name: stock.stock_name,
                          ticker: stock.ticker,
                          price: typeof yesterdayClose === 'number' ? yesterdayClose : "N/A", // yesterday_close 값을 price로 사용
                          change: typeof changeRate === 'number' ? changeRate : 0.0,        // change_rate 값을 change로 사용
                      };
                  } catch (e) {
                      console.error(`티커 ${stock.ticker} 상세 정보 fetch 또는 처리 중 오류:`, e);
                      return { name: stock.stock_name, ticker: stock.ticker, price: "N/A", change: 0.0 };
                  }
              });

              // 모든 상세 정보 요청이 완료될 때까지 기다림
              const combinedDataResults = await Promise.allSettled(detailPromises);

              const finalChartData = combinedDataResults.map(result => {
                 if (result.status === 'fulfilled') {
                     return result.value;
                 } else {
                     console.error("Promise.allSettled 처리 중 예상치 못한 실패:", result.reason);
                     return { name: "오류", ticker: "오류", price: "N/A", change: 0.0 };
                 }
              });

              setAllChartData(finalChartData); // 최종 데이터로 상태 업데이트

          } catch (e) {
              console.error("차트 데이터 로딩 중 전체 오류 발생:", e);
              setError(`데이터 로딩 실패: ${e.message}`);
              setAllChartData([]);
          }
      };

       // 2. 변동순 데이터 로딩 (기존 예시 데이터 사용 - 티커 추가 필요)
  const setupChangeRankData = () => {
    // 예시: 변동순 데이터 (isFavorite 속성 제거, ticker 추가)
    // !!! 중요: 실제로는 이 데이터도 서버에서 ticker를 포함하여 가져와야 합니다. !!!
    const fetchedChangeRankData = [
      { name: "카카오페이", ticker: "377300", change: 5.5 }, // 예시 티커
      { name: "네이버", ticker: "035420", change: 4.2 },     // 예시 티커
      { name: "HMM", ticker: "011200", change: 3.3 },        // 예시 티커
      { name: "하이브", ticker: "352820", change: 3.1 },     // 예시 티커
      { name: "두산밥캣", ticker: "241560", change: 2.8 },   // 예시 티커
      { name: "고려아연", ticker: "010130", change: 2.5 },   // 예시 티커
      { name: "셀트리온", ticker: "068270", change: 2.1 },   // 예시 티커
      { name: "LG디스플레이", ticker: "034220", change: 2.0 },// 예시 티커
      { name: "아모레퍼시픽", ticker: "090430", change: 1.8 },// 예시 티커
      { name: "KT&G", ticker: "033780", change: 1.2 },       // 예시 티커
      { name: "대한항공", ticker: "003490", change: 1.1 },   // 예시 티커
      { name: "SK이노베이션", ticker: "096770", change: 1.0 },// 예시 티커
      // ... (더 많은 변동순 데이터)
    ].sort((a, b) => b.change - a.change);
    setAllChangeRankData(fetchedChangeRankData);
  };

      // 데이터 로딩 실행
      fetchChartData().then(() => {
          // 차트 데이터 로딩이 완료된 후 변동순 데이터 설정 및 로딩 상태 종료
          setupChangeRankData();
          setLoading(false); // 모든 데이터 로딩 완료
      });

    }, []); // 컴포넌트 마운트 시 1회 실행

    // --- 계산된 값 ---
    // (이하 코드는 변경 없음)
    const totalPages = Math.ceil(allChartData.length / ITEMS_PER_PAGE);
    const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
    const endIndex = startIndex + ITEMS_PER_PAGE;
    const currentChartData = allChartData.slice(startIndex, endIndex);

    // --- 이벤트 핸들러 ---
    // (이하 코드는 변경 없음)
    const handleFavoriteClick = (stockName) => {
      console.log(`${stockName} 찜 상태 변경됨`);
    };

    const handlePageChange = (page) => {
      if (page === 'prev') {
        setCurrentPage((prev) => Math.max(prev - 1, 1));
      } else if (page === 'next') {
        setCurrentPage((prev) => Math.min(prev + 1, totalPages));
      } else {
        setCurrentPage(page);
      }
      console.log(`페이지 ${page === 'prev' ? currentPage - 1 : page === 'next' ? currentPage + 1 : page}로 이동 요청됨`);
    };

    // (renderPageNumbers 함수는 변경 없음)
    const renderPageNumbers = () => {
      const pageNumbers = [];
      const maxPagesToShow = 5;

      if (totalPages <= maxPagesToShow + 1) {
        for (let i = 1; i <= totalPages; i++) {
          pageNumbers.push(
            <button
              key={i}
              className={i === currentPage ? 'active' : ''}
              onClick={() => handlePageChange(i)}
            >
              {i}
            </button>
          );
        }
      } else {
        let startPage = Math.max(1, currentPage - Math.floor(maxPagesToShow / 2));
        let endPage = Math.min(totalPages, startPage + maxPagesToShow - 1);

        if (endPage === totalPages) {
            startPage = Math.max(1, totalPages - maxPagesToShow + 1);
        }

        if (startPage > 1) {
          pageNumbers.push(
            <button key={1} onClick={() => handlePageChange(1)}>1</button>
          );
          if (startPage > 2) {
            pageNumbers.push(<span key="start-ellipsis">...</span>);
          }
        }

        for (let i = startPage; i <= endPage; i++) {
          pageNumbers.push(
            <button
              key={i}
              className={i === currentPage ? 'active' : ''}
              onClick={() => handlePageChange(i)}
            >
              {i}
            </button>
          );
        }

        if (endPage < totalPages) {
          if (endPage < totalPages - 1) {
            pageNumbers.push(<span key="end-ellipsis">...</span>);
          }
          pageNumbers.push(
            <button key={totalPages} onClick={() => handlePageChange(totalPages)}>{totalPages}</button>
          );
        }
      }
      return pageNumbers;
    };


    // --- 렌더링 ---
    // 로딩 및 에러 상태 표시 추가
    if (loading) {
      return <div className="stock-table loading">데이터를 불러오는 중입니다...</div>;
    }

    if (error) {
      return <div className="stock-table error">오류: {error}</div>;
    }

    return (
      <div className="stock-table">
        {/* (이하 JSX 구조는 변경 없음) */}
        <div className="title">
          <h2 className="title-1">차트</h2>
          <h2 className="title-2">변동 순</h2>
        </div>

        <div className="table-container">
          {/* 차트 테이블 */}
          <div className="chart-table">
            <div style={{ flexGrow: 1, overflowY: 'auto', position: 'relative' }}>
                <table>
                  <thead>
                    <tr>
                      <th className="rank-header"></th>
                      <th>종목</th>
                      <th>현재가</th>
                      <th>등락률</th>
                      <th className="favorite-header"></th>
                    </tr>
                  </thead>
                  <tbody>
                    {currentChartData.map((stock, index) => (
                      <tr key={stock.name} className="stock-row">
                        <td className="rank-cell">{(currentPage - 1) * ITEMS_PER_PAGE + index + 1}</td>
                        {/* 종목명 + 로고 표시 수정 */}
                        <td className="stock-name-cell">
                          <img
                            src={`http://192.168.0.18:5000/logo/kr/${stock.ticker}`}
                            alt={`${stock.name} 로고`}
                            className="stock-logo"
                            onError={(e) => { e.target.style.display = 'none'; }} // 로딩 실패 시 숨김
                          />
                          <span>{stock.name}</span>
                        </td>
                        <td>
                          {typeof stock.price === 'number'
                            ? `${stock.price.toLocaleString('ko-KR')}원`
                            : stock.price}
                        </td>
                        <td className={stock.change >= 0 ? "positive" : "negative"}>
                          {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                        </td>
                        <td className="favorite-cell">
                          <span
                            className="favorite-icon"
                            onClick={() => handleFavoriteClick(stock.name)}
                            title={`${stock.name} 찜하기`}
                          >
                            ♥
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
            </div>
            {/* 페이지네이션 컨트롤 */}
            {totalPages > 1 && (
              <div className="pagination-controls">
                <button onClick={() => handlePageChange('prev')} disabled={currentPage === 1}>&lt;</button>
                {renderPageNumbers()}
                <button onClick={() => handlePageChange('next')} disabled={currentPage === totalPages}>&gt;</button>
              </div>
            )}
          </div>

          {/* 세로선 */}
          <div className="vertical-line"></div>

          {/* 변동순 테이블 */}
          <div className="change-rank">
            <table>
              <thead>
                <tr>
                  <th>종목</th>
                  <th>등락률</th>
                  <th className="favorite-header"></th>
                </tr>
              </thead>
              <tbody>
                    {/* 변동순 데이터는 상위 N개만 표시 (10개) */}
                    {allChangeRankData.slice(0, 10).map((stock) => (
                      // key를 이름 대신 고유한 ticker로 변경하는 것이 더 안전할 수 있습니다.
                      // 단, 이름과 티커가 항상 1:1 매칭된다는 보장이 있다면 이름도 괜찮습니다.
                      <tr key={stock.ticker || stock.name} className="stock-row">
                        {/* 종목명 + 로고 표시 수정 */}
                        <td className="stock-name-cell">
                          {/* stock.ticker가 존재할 경우에만 로고 표시 */}
                          {stock.ticker && (
                            <img
                              src={`http://192.168.0.18:5000/logo/kr/${stock.ticker}`}
                              alt={`${stock.name} 로고`}
                              className="stock-logo"
                              onError={(e) => { e.target.style.display = 'none'; }} // 로딩 실패 시 숨김
                            />
                          )}
                          <span>{stock.name}</span>
                        </td>
                        <td className={stock.change >= 0 ? "positive" : "negative"}>
                          {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                        </td>
                        <td className="favorite-cell">
                          <span
                            className="favorite-icon"
                            onClick={() => handleFavoriteClick(stock.name)}
                            title={`${stock.name} 찜하기`}
                          >
                            ♥
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
