import React from 'react';
import './Header.css';
import { useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import { useUser } from '../contexts/UserContext';

export default function Header() {
  const { user, setUser } = useUser();

  // Î°úÍ∑∏Ïù∏ Î°úÏßÅ
  const login = useGoogleLogin({
    onSuccess: async tokenResponse => {
      try {
        // 1Îã®Í≥Ñ: Google ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏöîÏ≤≠
        const res = await axios.get('https://www.googleapis.com/oauth2/v3/userinfo', {
          headers: {
            Authorization: `Bearer ${tokenResponse.access_token}`,
          },
        });

        const userInfo = res.data;
        setUser(userInfo); // ContextÏóê ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï†ÄÏû•

        // 2Îã®Í≥Ñ: Î∞±ÏóîÎìúÏóê ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï†ÑÏÜ° ‚Üí JWT Î∞úÍ∏â
        const jwtRes = await axios.post('http://localhost:5000/login', userInfo);

        if (jwtRes.data && jwtRes.data.token) {
          // 3Îã®Í≥Ñ: JWT ÌÜ†ÌÅ∞ Ï†ÄÏû•
          localStorage.setItem('jwt_token', jwtRes.data.token);
          console.log("‚úÖ JWT ÌÜ†ÌÅ∞ Ï†ÄÏû• ÏôÑÎ£å");
        } else {
          console.warn("‚ö†Ô∏è JWT ÌÜ†ÌÅ∞Ïù¥ ÏùëÎãµÏóê ÏóÜÏäµÎãàÎã§.");
        }

      } catch (error) {
        console.error('‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏöîÏ≤≠ ÎòêÎäî Î∞±ÏóîÎìú Ï†ÑÏÜ° Ïã§Ìå®:', error);
      }
    },
    onError: err => console.error('‚ùå Î°úÍ∑∏Ïù∏ Ïã§Ìå®:', err),
  });

  // Î°úÍ∑∏ÏïÑÏõÉ Î°úÏßÅ
  const logout = () => {
    setUser(null);
    localStorage.removeItem('jwt_token'); // Ï†ÄÏû•Îêú ÌÜ†ÌÅ∞ Ï†úÍ±∞
    console.log("üö™ Î°úÍ∑∏ÏïÑÏõÉ ÏôÑÎ£å");
  };

  return (
    <header className="header">
      <input className="search-input" placeholder="üîç Í≤ÄÏÉâ" />
      {user ? (
        <div className="user-info">
          <img src={user.picture} alt="ÌîÑÎ°úÌïÑ" className="user-pic" />
          <span>{user.name} Îãò</span>
          <button className="logout-btn" onClick={logout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
        </div>
      ) : (
        <button className="login-btn" onClick={() => login()}>Î°úÍ∑∏Ïù∏</button>
      )}
    </header>
  );
}
