import React, { useState } from 'react'; // useState 임포트
import { HomeIcon, HeartIcon, ClockIcon } from '@heroicons/react/24/solid';
import logo from './img/logo.png';

import './Sidebar.css';

export default function Sidebar() {
  // '관심' 영역 확장 상태를 관리하는 state (초기값: false)
  const [isFavoriteExpanded, setIsFavoriteExpanded] = useState(false);

  // '관심' 영역 클릭 시 상태를 토글하는 함수
  const handleFavoriteClick = () => {
    setIsFavoriteExpanded(prevState => !prevState);
  };

  return (
    <aside className="sidebar">
      <div className="logo">
        <img src={logo} alt="로고" className="logo-img" />
        <span className="web-name"></span>
      </div>
      {/*
      <div className="icon-text">
        <HomeIcon className="icon" />
        <span className="icon-label">홈</span>
      </div>
      */}

      {/* '관심' 영역 */}
      <div
        // isFavoriteExpanded 상태에 따라 'expanded' 클래스를 추가/제거
        className={`icon-text ${isFavoriteExpanded ? 'expanded' : ''}`}
        onClick={handleFavoriteClick} // 클릭 핸들러 연결
      >
        <HeartIcon className="icon" />
        <span className="icon-label">관심</span>
      </div>

      {/* '최근 본' 영역 */}
      <div className="icon-text">
        <ClockIcon className="icon" />
        <span className="icon-label">최근 본</span>
      </div>
    </aside>
  );
}
