import React, { useState, useEffect } from "react";
import "./StockTable.css";
import { useNavigate } from 'react-router-dom'; // 1. useNavigate 훅 가져오기

// import { FaRegHeart, FaHeart } from 'react-icons/fa'; // react-icons 사용 예시

