    const [error, setError] = useState(null); // 에러 상태 추가

    // --- 상수 ---
    const ITEMS_PER_PAGE = 10;
    const IP = "172.17.153.62:8080"; // 데이터 요청용 IP
  

    const navigate = useNavigate(); // 2. useNavigate 초기화

    // --- 데이터 로딩 ---
    useEffect(() => {
        const fetchChartData = async () => {
            try {
                const listResponse = await fetch(`http://${IP}/recommend/kr`);
                if (!listResponse.ok) {
                    throw new Error(`종목 목록 로딩 실패: ${listResponse.status}`);
                }
                const initialStockList = await listResponse.json();

                if (!Array.isArray(initialStockList) || initialStockList.length === 0) {
                    console.log("추천 종목 목록이 비어있습니다.");
                    setAllChartData([]);
                    setLoading(false); // 로딩 완료 처리
                    return;
                }

                const detailPromises = initialStockList.map(async (stock) => {
                    if (!stock.ticker || !stock.stock_name) {
                        console.warn("종목 목록 데이터에 ticker 또는 stock_name이 없습니다:", stock);
                        return { name: stock.stock_name || "이름 없음", ticker: stock.ticker || "티커 없음", price: "N/A", change: 0.0 };
                    }

                    try {
                        const detailResponse = await fetch(`http://${IP}/changerate/kr/${stock.ticker}`);
                        if (!detailResponse.ok) {
                            console.warn(`티커 ${stock.ticker} 상세 정보 로딩 실패: ${detailResponse.status}`);
                            return { name: stock.stock_name, ticker: stock.ticker, price: "N/A", change: 0.0 };
                        }
                        const details = await detailResponse.json();
                        const yesterdayClose = details.yesterday_close;
                        const changeRate = details.change_rate;

                        return {
                            name: stock.stock_name,
                            ticker: stock.ticker,
                            price: typeof yesterdayClose === 'number' ? yesterdayClose : "N/A",
                            change: typeof changeRate === 'number' ? changeRate : 0.0,
                        };
                    } catch (e) {
                        console.error(`티커 ${stock.ticker} 상세 정보 fetch 또는 처리 중 오류:`, e);
                        return { name: stock.stock_name, ticker: stock.ticker, price: "N/A", change: 0.0 };
                    }
                });

                const combinedDataResults = await Promise.allSettled(detailPromises);
                const finalChartData = combinedDataResults.map(result => {
                    if (result.status === 'fulfilled') {
                        return result.value;
                    } else {
                        console.error("Promise.allSettled 처리 중 예상치 못한 실패:", result.reason);
                        return { name: "오류", ticker: "오류", price: "N/A", change: 0.0 };
                    }
                });
                setAllChartData(finalChartData);
            } catch (e) {
                console.error("차트 데이터 로딩 중 전체 오류 발생:", e);
                setError(`데이터 로딩 실패: ${e.message}`);
                setAllChartData([]);
            }
        };

        const setupChangeRankData = () => {
            const fetchedChangeRankData = [
                { name: "카카오페이", ticker: "377300", price: 0, change: 5.5 }, // price는 예시, 실제 데이터 필요
                { name: "네이버", ticker: "035420", price: 0, change: 4.2 },
                { name: "HMM", ticker: "011200", price: 0, change: 3.3 },
                { name: "하이브", ticker: "352820", price: 0, change: 3.1 },
                { name: "두산밥캣", ticker: "241560", price: 0, change: 2.8 },
                { name: "고려아연", ticker: "010130", price: 0, change: 2.5 },
                { name: "셀트리온", ticker: "068270", price: 0, change: 2.1 },
                { name: "LG디스플레이", ticker: "034220", price: 0, change: 2.0 },
                { name: "아모레퍼시픽", ticker: "090430", price: 0, change: 1.8 },
                { name: "KT&G", ticker: "033780", price: 0, change: 1.2 },
                { name: "대한항공", ticker: "003490", price: 0, change: 1.1 },
                { name: "SK이노베이션", ticker: "096770", price: 0, change: 1.0 },
            ].sort((a, b) => b.change - a.change);
            // 변동순 데이터에도 가격 정보가 있다면 함께 전달하는 것이 좋습니다.
            // 없다면 ChartPage에서 API 호출을 통해 가져와야 합니다.
            // 여기서는 일단 price를 0 또는 "N/A"로 설정합니다.
            setAllChangeRankData(fetchedChangeRankData.map(item => ({ ...item, price: item.price || "N/A" })));
        };

        setLoading(true);
        fetchChartData().finally(() => {
            setupChangeRankData();
            setLoading(false);
        });
    }, []);

    // --- 계산된 값 ---
    const totalPages = Math.ceil(allChartData.length / ITEMS_PER_PAGE);
    const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
    const endIndex = startIndex + ITEMS_PER_PAGE;
    const currentChartData = allChartData.slice(startIndex, endIndex);

    // --- 이벤트 핸들러 ---
    const handleFavoriteClick = (e, stockName) => {
        e.stopPropagation(); // 행 클릭 이벤트 전파 방지
        console.log(`${stockName} 찜 상태 변경됨`);
    };

    const handlePageChange = (page) => {
        if (page === 'prev') {
            setCurrentPage((prev) => Math.max(prev - 1, 1));
        } else if (page === 'next') {
            setCurrentPage((prev) => Math.min(prev + 1, totalPages));
        } else {
            setCurrentPage(page);
        }
    };

    // 3. 행 클릭 시 Chart 페이지로 이동하는 핸들러
    const handleStockRowClick = (stock) => {
        if (stock && stock.ticker && stock.ticker !== "티커 없음" && stock.ticker !== "오류") {
            // stock.price가 문자열 "N/A"일 수 있으므로, 그대로 전달하거나 숫자형으로 변환 로직 필요
            const priceToSend = typeof stock.price === 'number' ? stock.price : "N/A";
            navigate(`/chart/${stock.ticker}`, {
                state: {
                    stockName: stock.name,
                    stockPrice: priceToSend,
                    // 로고 URL은 ChartPage에서 ticker를 기반으로 생성하거나, 여기서 직접 전달할 수도 있습니다.
                    // 여기서는 ChartPage에서 생성하도록 ticker만 확실히 전달합니다.
                }
            });
            console.log(`차트 페이지로 이동: ${stock.name} (${stock.ticker})`);
        } else {
            console.warn("유효하지 않은 종목 정보로 차트 페이지로 이동할 수 없습니다:", stock);
        }
    };

    const renderPageNumbers = () => {
        const pageNumbers = [];
        const maxPagesToShow = 5;

        if (totalPages <= maxPagesToShow + 1) {
            for (let i = 1; i <= totalPages; i++) {
                pageNumbers.push(
                    <button
                        key={i}
                        className={i === currentPage ? 'active' : ''}
                        onClick={() => handlePageChange(i)}
                    >
                        {i}
                    </button>
                );
            }
        } else {
            let startPage = Math.max(1, currentPage - Math.floor(maxPagesToShow / 2));
            let endPage = Math.min(totalPages, startPage + maxPagesToShow - 1);

            if (endPage === totalPages) {
                startPage = Math.max(1, totalPages - maxPagesToShow + 1);
            }

            if (startPage > 1) {
                pageNumbers.push(<button key={1} onClick={() => handlePageChange(1)}>1</button>);
                if (startPage > 2) pageNumbers.push(<span key="start-ellipsis">...</span>);
            }

            for (let i = startPage; i <= endPage; i++) {
                pageNumbers.push(
                    <button
                        key={i}
                        className={i === currentPage ? 'active' : ''}
                        onClick={() => handlePageChange(i)}
                    >
                        {i}
                    </button>
                );
            }

            if (endPage < totalPages) {
                if (endPage < totalPages - 1) pageNumbers.push(<span key="end-ellipsis">...</span>);
                pageNumbers.push(<button key={totalPages} onClick={() => handlePageChange(totalPages)}>{totalPages}</button>);
            }
        }
        return pageNumbers;
    };

    if (loading) {
        return <div className="stock-table loading">데이터를 불러오는 중입니다...</div>;
    }

    if (error) {
        return <div className="stock-table error">오류: {error}</div>;
    }

    return (
        <div className="stock-table">
            <div className="title">
                <h2 className="title-1">차트</h2>
                <h2 className="title-2">변동 순</h2>
            </div>
            <div className="table-container">
                <div className="chart-table">
                    <div style={{ flexGrow: 1, overflowY: 'auto', position: 'relative' }}>
                        <table>
                            <thead>
                                <tr>
                                    <th className="rank-header"></th>
                                    <th>종목</th>
                                    <th>현재가</th>
                                    <th>등락률</th>
                                    <th className="favorite-header"></th>
                                </tr>
                            </thead>
                            <tbody>
                                {currentChartData.map((stock, index) => (
                                    // 4. 행에 onClick 이벤트 핸들러 추가
                                    <tr key={stock.ticker || stock.name} className="stock-row" onClick={() => handleStockRowClick(stock)}>
                                        <td className="rank-cell">{(currentPage - 1) * ITEMS_PER_PAGE + index + 1}</td>
                                        <td className="stock-name-cell">
                                            {/* 차트 테이블의 로고는 데이터 가져오는 IP와 동일한 것으로 가정. 만약 다르다면 LOGO_IP 사용 */}
                                            {stock.ticker && stock.ticker !== "티커 없음" && stock.ticker !== "오류" && (
                                                <img
                                                    src={`http://${IP}/logo/kr/${stock.ticker}`} // LOGO_IP 사용
                                                    alt={`${stock.name} 로고`}
                                                    className="stock-logo"
                                                    onError={(e) => { e.target.style.display = 'none'; }}
                                                />
                                            )}
                                            <span>{stock.name}</span>
                                        </td>
                                        <td>
                                            {typeof stock.price === 'number'
                                                ? `${stock.price.toLocaleString('ko-KR')}원`
                                                : stock.price}
                                        </td>
                                        <td className={stock.change >= 0 ? "positive" : "negative"}>
                                            {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                                        </td>
                                        <td className="favorite-cell">
                                            <span
                                                className="favorite-icon"
                                                onClick={(e) => handleFavoriteClick(e, stock.name)}
                                                title={`${stock.name} 찜하기`}
                                            >
                                                ♥
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    {totalPages > 1 && (
                        <div className="pagination-controls">
                            <button onClick={() => handlePageChange('prev')} disabled={currentPage === 1}>&lt;</button>
                            {renderPageNumbers()}
                            <button onClick={() => handlePageChange('next')} disabled={currentPage === totalPages}>&gt;</button>
                        </div>
                    )}
                </div>

                <div className="vertical-line"></div>

                <div className="change-rank">
                    <table>
                        <thead>
                            <tr>
                                <th>종목</th>
                                <th>등락률</th>
                                <th className="favorite-header"></th>
                            </tr>
                        </thead>
                        <tbody>
                            {allChangeRankData.slice(0, 10).map((stock) => (
                                // 4. 변동순 테이블 행에도 onClick 이벤트 핸들러 추가
                                <tr key={stock.ticker || stock.name} className="stock-row" onClick={() => handleStockRowClick(stock)}>
                                    <td className="stock-name-cell">
                                        {stock.ticker && (
                                            <img
                                                src={`http://${IP}/logo/kr/${stock.ticker}`} // LOGO_IP 사용 (기존 코드와 동일)
                                                alt={`${stock.name} 로고`}
                                                className="stock-logo"
                                                onError={(e) => { e.target.style.display = 'none'; }}
                                            />
                                        )}
                                        <span>{stock.name}</span>
                                    </td>
                                    <td className={stock.change >= 0 ? "positive" : "negative"}>
                                        {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(1)}%
                                    </td>
                                    <td className="favorite-cell">
                                        <span
                                            className="favorite-icon"
                                            onClick={(e) => handleFavoriteClick(e, stock.name)}
                                            title={`${stock.name} 찜하기`}
                                        >
                                            ♥
                                        </span>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}
